let type;
let value = parseFloat(msg.payload);

// Xác định loại dữ liệu từ topic
if (msg.topic.includes("humid")) {
    type = "Humidity";
} else if (msg.topic.includes("temp")) {
    type = "Temperature";
} else if (msg.topic.includes("pm25")) {
    type = "PM2_5";  // Sửa lại PM2.5
} else if (msg.topic.includes("pm10")) {
    type = "PM10";
} else {
    type = "Unknown"; // Kiểm tra nếu topic không xác định
}

// Lưu giá trị vào flow context theo từng loại
flow.set(type, value);

// Lấy giá trị hiện có từ flow context
let humidity = flow.get("Humidity");
let temp = flow.get("Temperature");
let pm25 = flow.get("PM2_5");
let pm10 = flow.get("PM10");

// Chỉ gửi dữ liệu khi đủ cả 4 giá trị
if (humidity !== undefined && temp !== undefined && pm25 !== undefined && pm10 !== undefined) {
    // Định dạng dữ liệu cho chart
    let pmData = [
        { x: new Date().toISOString(), y: pm25 },
        { x: new Date().toISOString(), y: pm10 }
    ];

    let chartData = {
        series: ["PM2.5", "PM10"],
        data: [pmData[0], pmData[1]],
        labels: [new Date().toISOString()]
    };

    // Dữ liệu cho các gauge
    let gaugesData = {
        PM2_5: pm25,
        PM10: pm10,
        Temperature: temp,
        Humidity: humidity
    };

    // Trả lại dữ liệu dưới định dạng cho từng output
    return [
        { payload: pm25 },  // Output 1: PM2.5 (chỉ gửi giá trị)
        { payload: pm10 },  // Output 2: PM10 (chỉ gửi giá trị)
        { payload: temp },  // Output 3: Temperature (chỉ gửi giá trị)
        { payload: humidity },  // Output 4: Humidity (chỉ gửi giá trị)
        { payload: chartData },  // Output 5: Dữ liệu cho chart (PM2.5, PM10)
        { payload: gaugesData }  // Output 6: Gauges data
    ];
} else {
    return null;  // Trả về null nếu thiếu một trong các giá trị cần thiết
}

// Function 1: Tạo API URL
const channelID = "3030128";
const readAPI = "A2AAQJ1EF8O43YAP";
const results = 20; // Số records muốn lấy (có thể thay đổi)

msg.url = `https://api.thingspeak.com/channels/${channelID}/feeds.json?api_key=${readAPI}&results=${results}`;
msg.method = "GET";

return msg;

// ===================================

// Function 2: Format dữ liệu cho table
const feeds = msg.payload.feeds;
let tableData = [];

feeds.forEach(feed => {
    const timestamp = new Date(feed.created_at);
    // Format thời gian theo múi giờ Việt Nam
    const timeString = timestamp.toLocaleTimeString('vi-VN', {
        hour: '2-digit',
        minute: '2-digit',
        timeZone: 'Asia/Ho_Chi_Minh'
    });

    // Kiểm tra và format từng field
    const pm25 = feed.field1 ? parseFloat(feed.field1).toFixed(1) : "-";
    const pm10 = feed.field2 ? parseFloat(feed.field2).toFixed(1) : "-";  
    const humidity = feed.field3 ? parseFloat(feed.field3).toFixed(1) : "-";
    const temperature = feed.field4 ? parseFloat(feed.field4).toFixed(1) : "-";

    tableData.push({
        "Time": timeString,
        "PM2.5": pm25,
        "PM10": pm10,
        "Temperature": temperature,
        "Humidity": humidity
    });
});

// Sắp xếp theo thời gian mới nhất trước (reverse)
tableData.reverse();

msg.payload = tableData;
return msg;

// ===================================

// Function 3: Auto refresh (cho inject node)
// Trigger này để tự động lấy dữ liệu mỗi 5 phút
// Chỉ cần set inject node với interval: 5 minutes
// Hoặc dùng function này nếu muốn logic phức tạp hơn

const now = new Date();
node.warn(`Refreshing data at: ${now.toLocaleString('vi-VN')}`);

// Gửi trigger để lấy dữ liệu mới
return msg;

// Tính trung bình và đánh giá status
if (!msg.payload || msg.payload.length === 0) {
    msg.averages = {};
    return msg;
}

const data = msg.payload;
let pm25Sum = 0, pm10Sum = 0, tempSum = 0, humidSum = 0;
let count = data.length;

data.forEach(item => {
    pm25Sum += parseFloat(item.PM25) || 0;
    pm10Sum += parseFloat(item.PM10) || 0;
    tempSum += parseFloat(item.Temperature) || 0;
    humidSum += parseFloat(item.Humidity) || 0;
});

const avgPM25 = (pm25Sum / count).toFixed(1);
const avgPM10 = (pm10Sum / count).toFixed(1);
const avgTemp = (tempSum / count).toFixed(1);
const avgHumid = (humidSum / count).toFixed(0);

// Đánh giá status theo tiêu chuẩn WHO/EPA
function getPM25Status(value) {
    if (value <= 12) return "Good";
    if (value <= 35.4) return "Moderate";
    if (value <= 55.4) return "Unhealthy for Sensitive";
    if (value <= 150.4) return "Unhealthy";
    if (value <= 250.4) return "Very Unhealthy";
    return "Hazardous";
}

function getPM10Status(value) {
    if (value <= 54) return "Good";
    if (value <= 154) return "Moderate";
    if (value <= 254) return "Unhealthy for Sensitive";
    if (value <= 354) return "Unhealthy";
    if (value <= 424) return "Very Unhealthy";
    return "Hazardous";
}

function getTempStatus(value) {
    if (value >= 18 && value <= 27) return "Comfortable";
    if ((value >= 15 && value < 18) || (value > 27 && value <= 32)) return "Moderate";
    if ((value >= 10 && value < 15) || (value > 32 && value <= 38)) return "Uncomfortable";
    return "Extreme";
}

function getHumidityStatus(value) {
    if (value >= 40 && value <= 60) return "Comfortable";
    if ((value >= 30 && value < 40) || (value > 60 && value <= 70)) return "Moderate";
    if ((value >= 20 && value < 30) || (value > 70 && value <= 80)) return "Uncomfortable";
    return "Extreme";
}

msg.averages = {
    pm25: { value: avgPM25, status: getPM25Status(parseFloat(avgPM25)) },
    pm10: { value: avgPM10, status: getPM10Status(parseFloat(avgPM10)) },
    temp: { value: avgTemp, status: getTempStatus(parseFloat(avgTemp)) },
    humidity: { value: avgHumid, status: getHumidityStatus(parseInt(avgHumid)) }
};

return msg;

if (msg.payload === 'led_toggle') {
    let state = flow.get("led_state") || "OFF";
    if (state === "OFF") {
        state = "ON";
        msg.payload = "LED_ON";
    } else {
        state = "OFF";
        msg.payload = "LED_OFF";
    }
    flow.set("led_state", state);
    msg.ledState = state;
    msg.topic = "/23127354-23127388-23127435/led-buzzer";
}
else if (msg.payload === 'buzzer_toggle') {
    let state = flow.get("buzzer_state") || "OFF";
    if (state === "OFF") {
        state = "ON";
        msg.payload = "BUZZER_ON";
    } else {
        state = "OFF";
        msg.payload = "BUZZER_OFF";
    }
    flow.set("buzzer_state", state);
    msg.buzzerState = state;
    msg.topic = "/23127354-23127388-23127435/led-buzzer";
}
return msg;

// Đảm bảo có label và payload là số
let label = msg.label;
let value = msg.payload;

if (!label || typeof value !== "number") {
    msg.payload = {
        alert: "sai label",
    };
    return msg;  // Bỏ qua nếu không hợp lệ
}

// Lưu giá trị vào flow context
flow.set(label, value);

// Lấy lại giá trị từ bộ nhớ
let pm25 = flow.get("PM2_5");
let pm10 = flow.get("PM10");

// Chỉ xử lý khi đã có đủ cả hai
if (pm25 === undefined || pm10 === undefined) {
    msg.payload = {
        alert: "Chua du hai du lieu",
        PM2_5: pm25,
        PM10: pm10,
        timestamp: new Date().toISOString()
    };
    return msg;
}

// Kiểm tra trạng thái cảnh báo
let isAlert = (pm25 > 35.4 || pm10 > 154);
let wasAlert = flow.get("alert_state") || false;

if (isAlert && !wasAlert) {
    flow.set("alert_state", true);
    msg.payload = "ALERT";
    return msg;
} else if (!isAlert && wasAlert) {
    flow.set("alert_state", false);
    msg.payload = {
        alert: "SAFE",
        PM2_5: pm25,
        PM10: pm10,
        timestamp: new Date().toISOString()
    };
    return msg;
}

// Không thay đổi trạng thái → không gửi gì
msg.payload = {
    alert: "no change state",
    PM2_5: pm25,
    PM10: pm10,
    timestamp: new Date().toISOString()
};
return msg;

// Lấy giá trị PM2_5 và PM10 từ flow context
let pm25 = flow.get("PM2_5");
let pm10 = flow.get("PM10");


// Chuẩn bị payload chỉ chứa dữ liệu
msg.payload = `PM2.5: ${pm25 || 0} µgm, PM10: ${pm10 || 0} µgm`;

return msg;

// msg.payload sẽ là 'dust', 'temp' hoặc 'humid' từ template Display
msg.topic = "/23127354-23127388-23127435/screen";
return msg;

const feeds = msg.payload.feeds;

let messages = [];

feeds.forEach(feed => {
    const timestamp = new Date(feed.created_at).getTime();

    if (feed.field1 !== null && feed.field1 !== undefined && feed.field1 !== "") {
        messages.push({
            payload: parseFloat(feed.field1),
            topic: "PM2.5",
            timestamp: timestamp
        });
    }

    if (feed.field2 !== null && feed.field2 !== undefined && feed.field2 !== "") {
        messages.push({
            payload: parseFloat(feed.field2),
            topic: "PM10",
            timestamp: timestamp
        });
    }
});

return [messages];

// Lấy dữ liệu feeds từ ThingSpeak
const feeds = msg.payload.feeds;

// Kiểm tra dữ liệu đầu vào
if (!feeds || !Array.isArray(feeds)) {
    node.error("Không có dữ liệu feeds hợp lệ");
    return null;
}

let pm25Values = [];
let pm10Values = [];

// Thu thập tất cả giá trị hợp lệ
feeds.forEach(feed => {
    // Thu thập PM2.5 values
    if (feed.field1 !== null && feed.field1 !== undefined && feed.field1 !== "") {
        const pm25 = parseFloat(feed.field1);
        if (!isNaN(pm25)) {
            pm25Values.push(pm25);
        }
    }

    // Thu thập PM10 values  
    if (feed.field2 !== null && feed.field2 !== undefined && feed.field2 !== "") {
        const pm10 = parseFloat(feed.field2);
        if (!isNaN(pm10)) {
            pm10Values.push(pm10);
        }
    }
});

// Tính trung bình
let pm25Avg = 0;
let pm10Avg = 0;

if (pm25Values.length > 0) {
    pm25Avg = pm25Values.reduce((sum, val) => sum + val, 0) / pm25Values.length;
}

if (pm10Values.length > 0) {
    pm10Avg = pm10Values.reduce((sum, val) => sum + val, 0) / pm10Values.length;
}

// Làm tròn đến 1 chữ số thập phân
pm25Avg = Math.round(pm25Avg * 10) / 10;
pm10Avg = Math.round(pm10Avg * 10) / 10;

// Log kết quả
node.warn(`PM2.5 Average: ${pm25Avg} μg/m³ (từ ${pm25Values.length} samples)`);
node.warn(`PM10 Average: ${pm10Avg} μg/m³ (từ ${pm10Values.length} samples)`);

// Tạo payload cho Template node (chỉ 1 output)
msg.payload = {
    pm25_avg: pm25Avg,
    pm10_avg: pm10Avg,
    pm25_count: pm25Values.length,
    pm10_count: pm10Values.length
};

return msg;

var username = msg.payload.username;
msg.original_password = msg.payload.password; // Lưu pass gốc để so sánh
msg.method = "GET";
msg.url = "https://iot-user-4e30c-default-rtdb.asia-southeast1.firebasedatabase.app/users/" + username + ".json";
return msg;

if (!msg.payload) {
    msg.payload = { status: "Log:   ❌ User not found", loggedIn: false };
    context.set("loggedIn", false);
    return [null, msg];
}

if (msg.payload.password === msg.original_password) {
    msg.payload = { status: "✅ Login successful", loggedIn: true };
    context.set("loggedIn", true);
    return [msg, null];
} else {
    msg.payload = { status: "Log:   ❌ Wrong password", loggedIn: false };
    context.set("loggedIn", false);
    return [null, msg];
}

if (!msg.payload || !msg.payload.feeds) {
    msg.payload = [];
    return msg;
}

const feeds = msg.payload.feeds;
let tableData = [];

feeds.forEach(feed => {
    const timestamp = new Date(feed.created_at);
    const timeString = timestamp.toLocaleString('vi-VN', {
        day: '2-digit',
        month: '2-digit', 
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        timeZone: 'Asia/Ho_Chi_Minh'
    });

    tableData.push({
        "Time": timeString,
        "PM25": feed.field1,  // Thay đổi key thành "PM25"
        "PM10": feed.field2,
        "Temperature": feed.field3,
        "Humidity": feed.field4
    });
});

tableData.reverse();
msg.payload = tableData;
return msg;
