func:
let type;
let value = parseFloat(msg.payload);

// Xác định loại dữ liệu từ topic
if (msg.topic.includes("humid")) {
    type = "Humidity";
} else if (msg.topic.includes("temp")) {
    type = "Temperature";
} else if (msg.topic.includes("pm25")) {
    type = "PM2_5";  // Sửa lại PM2.5
} else if (msg.topic.includes("pm10")) {
    type = "PM10";
} else {
    type = "Unknown"; // Kiểm tra nếu topic không xác định
}

// Lưu giá trị vào flow context theo từng loại
flow.set(type, value);

// Lấy giá trị hiện có từ flow context
let humidity = flow.get("Humidity");
let temp = flow.get("Temperature");
let pm25 = flow.get("PM2_5");
let pm10 = flow.get("PM10");

// Chỉ gửi dữ liệu khi đủ cả 4 giá trị
if (humidity !== undefined && temp !== undefined && pm25 !== undefined && pm10 !== undefined) {
    // Định dạng dữ liệu cho chart
    let pmData = [
        { x: new Date().toISOString(), y: pm25 },
        { x: new Date().toISOString(), y: pm10 }
    ];

    let chartData = {
        series: ["PM2.5", "PM10"],
        data: [pmData[0], pmData[1]],
        labels: [new Date().toISOString()]
    };

    // Dữ liệu cho các gauge
    let gaugesData = {
        PM2_5: pm25,
        PM10: pm10,
        Temperature: temp,
        Humidity: humidity
    };

    // Trả lại dữ liệu dưới định dạng cho từng output
    return [
        { payload: pm25 },  // Output 1: PM2.5 (chỉ gửi giá trị)
        { payload: pm10 },  // Output 2: PM10 (chỉ gửi giá trị)
        { payload: temp },  // Output 3: Temperature (chỉ gửi giá trị)
        { payload: humidity },  // Output 4: Humidity (chỉ gửi giá trị)
        { payload: chartData },  // Output 5: Dữ liệu cho chart (PM2.5, PM10)
        { payload: gaugesData }  // Output 6: Gauges data
    ];
} else {
    return null;  // Trả về null nếu thiếu một trong các giá trị cần thiết
}

// Function 1: Tạo API URL
const channelID = "3030128";
const readAPI = "A2AAQJ1EF8O43YAP";
const results = 20; // Số records muốn lấy (có thể thay đổi)

msg.url = `https://api.thingspeak.com/channels/${channelID}/feeds.json?api_key=${readAPI}&results=${results}`;
msg.method = "GET";

return msg;

// ===================================

// Function 2: Format dữ liệu cho table
const feeds = msg.payload.feeds;
let tableData = [];

feeds.forEach(feed => {
    const timestamp = new Date(feed.created_at);
    // Format thời gian theo múi giờ Việt Nam
    const timeString = timestamp.toLocaleTimeString('vi-VN', {
        hour: '2-digit',
        minute: '2-digit',
        timeZone: 'Asia/Ho_Chi_Minh'
    });

    // Kiểm tra và format từng field
    const pm25 = feed.field1 ? parseFloat(feed.field1).toFixed(1) : "-";
    const pm10 = feed.field2 ? parseFloat(feed.field2).toFixed(1) : "-";  
    const humidity = feed.field3 ? parseFloat(feed.field3).toFixed(1) : "-";
    const temperature = feed.field4 ? parseFloat(feed.field4).toFixed(1) : "-";

    tableData.push({
        "Time": timeString,
        "PM2.5": pm25,
        "PM10": pm10,
        "Temperature": temperature,
        "Humidity": humidity
    });
});

// Sắp xếp theo thời gian mới nhất trước (reverse)
tableData.reverse();

msg.payload = tableData;
return msg;

// ===================================

// Function 3: Auto refresh (cho inject node)
// Trigger này để tự động lấy dữ liệu mỗi 5 phút
// Chỉ cần set inject node với interval: 5 minutes
// Hoặc dùng function này nếu muốn logic phức tạp hơn

const now = new Date();
node.warn(`Refreshing data at: ${now.toLocaleString('vi-VN')}`);

// Gửi trigger để lấy dữ liệu mới
return msg;

// Tính trung bình và đánh giá status
if (!msg.payload || msg.payload.length === 0) {
    msg.averages = {};
    return msg;
}

const data = msg.payload;
let pm25Sum = 0, pm10Sum = 0, tempSum = 0, humidSum = 0;
let count = data.length;

data.forEach(item => {
    pm25Sum += parseFloat(item.PM25) || 0;
    pm10Sum += parseFloat(item.PM10) || 0;
    tempSum += parseFloat(item.Temperature) || 0;
    humidSum += parseFloat(item.Humidity) || 0;
});

const avgPM25 = (pm25Sum / count).toFixed(1);
const avgPM10 = (pm10Sum / count).toFixed(1);
const avgTemp = (tempSum / count).toFixed(1);
const avgHumid = (humidSum / count).toFixed(0);

// Đánh giá status theo tiêu chuẩn WHO/EPA
function getPM25Status(value) {
    if (value <= 12) return "Good";
    if (value <= 35.4) return "Moderate";
    if (value <= 55.4) return "Unhealthy for Sensitive";
    if (value <= 150.4) return "Unhealthy";
    if (value <= 250.4) return "Very Unhealthy";
    return "Hazardous";
}

function getPM10Status(value) {
    if (value <= 54) return "Good";
    if (value <= 154) return "Moderate";
    if (value <= 254) return "Unhealthy for Sensitive";
    if (value <= 354) return "Unhealthy";
    if (value <= 424) return "Very Unhealthy";
    return "Hazardous";
}

function getTempStatus(value) {
    if (value >= 18 && value <= 27) return "Comfortable";
    if ((value >= 15 && value < 18) || (value > 27 && value <= 32)) return "Moderate";
    if ((value >= 10 && value < 15) || (value > 32 && value <= 38)) return "Uncomfortable";
    return "Extreme";
}

function getHumidityStatus(value) {
    if (value >= 40 && value <= 60) return "Comfortable";
    if ((value >= 30 && value < 40) || (value > 60 && value <= 70)) return "Moderate";
    if ((value >= 20 && value < 30) || (value > 70 && value <= 80)) return "Uncomfortable";
    return "Extreme";
}

msg.averages = {
    pm25: { value: avgPM25, status: getPM25Status(parseFloat(avgPM25)) },
    pm10: { value: avgPM10, status: getPM10Status(parseFloat(avgPM10)) },
    temp: { value: avgTemp, status: getTempStatus(parseFloat(avgTemp)) },
    humidity: { value: avgHumid, status: getHumidityStatus(parseInt(avgHumid)) }
};

return msg;

if (msg.payload === 'led_toggle') {
    let state = flow.get("led_state") || "OFF";
    if (state === "OFF") {
        state = "ON";
        msg.payload = "LED_ON";
    } else {
        state = "OFF";
        msg.payload = "LED_OFF";
    }
    flow.set("led_state", state);
    msg.ledState = state;
    msg.topic = "/23127354-23127388-23127435/led-buzzer";
}
else if (msg.payload === 'buzzer_toggle') {
    let state = flow.get("buzzer_state") || "OFF";
    if (state === "OFF") {
        state = "ON";
        msg.payload = "BUZZER_ON";
    } else {
        state = "OFF";
        msg.payload = "BUZZER_OFF";
    }
    flow.set("buzzer_state", state);
    msg.buzzerState = state;
    msg.topic = "/23127354-23127388-23127435/led-buzzer";
}
return msg;

// Đảm bảo có label và payload là số
let label = msg.label;
let value = msg.payload;

if (!label || typeof value !== "number") {
    msg.payload = {
        alert: "sai label",
    };
    return msg;  // Bỏ qua nếu không hợp lệ
}

// Lưu giá trị vào flow context
flow.set(label, value);

// Lấy lại giá trị từ bộ nhớ
let pm25 = flow.get("PM2_5");
let pm10 = flow.get("PM10");

// Chỉ xử lý khi đã có đủ cả hai
if (pm25 === undefined || pm10 === undefined) {
    msg.payload = {
        alert: "Chua du hai du lieu",
        PM2_5: pm25,
        PM10: pm10,
        timestamp: new Date().toISOString()
    };
    return msg;
}

// đoạn code cho led
let statePM25 = pm25 > 35.4 ? 2 : (pm25 > 12 ? 1 : 0);
let statePM10 = pm10 > 154 ? 2 : (pm10 > 54 ? 1 : 0);
let ledState = (statePM25 === 2 || statePM10 === 2) ? 2
    : (statePM25 === 1 || statePM10 === 1) ? 1
        : 0;

flow.set("led_state", ledState);
// Kiểm tra trạng thái cảnh báo
let isAlert = (pm25 > 35.4 || pm10 > 154);
let wasAlert = flow.get("alert_state") || false;

if (isAlert && !wasAlert) {
    flow.set("alert_state", true);
    msg.payload = "ALERT";
    flow.set("PM2_5", undefined);
    flow.set("PM10", undefined);
    return msg;
} else if (!isAlert && wasAlert) {
    flow.set("alert_state", false);
    msg.payload = {
        alert: "SAFE",
        PM2_5: pm25,
        PM10: pm10,
        timestamp: new Date().toISOString()
    };
    flow.set("PM2_5", undefined);
    flow.set("PM10", undefined);

    return msg;
}

// Không thay đổi trạng thái → không gửi gì
msg.payload = {
    alert: "no change state",
    PM2_5: pm25,
    PM10: pm10,
    timestamp: new Date().toISOString()
};
flow.set("PM2_5", undefined);
flow.set("PM10", undefined);

return msg;

// Lấy giá trị PM2_5 và PM10 từ flow context
let pm25 = flow.get("PM2_5");
let pm10 = flow.get("PM10");


// Chuẩn bị payload chỉ chứa dữ liệu
msg.payload = `PM2.5: ${pm25 || 0} µgm, PM10: ${pm10 || 0} µgm`;

return msg;

// msg.payload sẽ là 'dust', 'temp' hoặc 'humid' từ template Display
msg.topic = "/23127354-23127388-23127435/screen";
return msg;

const feeds = msg.payload.feeds;

let messages = [];

feeds.forEach(feed => {
    const timestamp = new Date(feed.created_at).getTime();

    if (feed.field1 !== null && feed.field1 !== undefined && feed.field1 !== "") {
        messages.push({
            payload: parseFloat(feed.field1),
            topic: "PM2.5",
            timestamp: timestamp
        });
    }

    if (feed.field2 !== null && feed.field2 !== undefined && feed.field2 !== "") {
        messages.push({
            payload: parseFloat(feed.field2),
            topic: "PM10",
            timestamp: timestamp
        });
    }
});

return [messages];

// Lấy dữ liệu feeds từ ThingSpeak
const feeds = msg.payload.feeds;

// Kiểm tra dữ liệu đầu vào
if (!feeds || !Array.isArray(feeds)) {
    node.error("Không có dữ liệu feeds hợp lệ");
    return null;
}

let pm25Values = [];
let pm10Values = [];

// Thu thập tất cả giá trị hợp lệ
feeds.forEach(feed => {
    // Thu thập PM2.5 values
    if (feed.field1 !== null && feed.field1 !== undefined && feed.field1 !== "") {
        const pm25 = parseFloat(feed.field1);
        if (!isNaN(pm25)) {
            pm25Values.push(pm25);
        }
    }

    // Thu thập PM10 values  
    if (feed.field2 !== null && feed.field2 !== undefined && feed.field2 !== "") {
        const pm10 = parseFloat(feed.field2);
        if (!isNaN(pm10)) {
            pm10Values.push(pm10);
        }
    }
});

// Tính trung bình
let pm25Avg = 0;
let pm10Avg = 0;

if (pm25Values.length > 0) {
    pm25Avg = pm25Values.reduce((sum, val) => sum + val, 0) / pm25Values.length;
}

if (pm10Values.length > 0) {
    pm10Avg = pm10Values.reduce((sum, val) => sum + val, 0) / pm10Values.length;
}

// Làm tròn đến 1 chữ số thập phân
pm25Avg = Math.round(pm25Avg * 10) / 10;
pm10Avg = Math.round(pm10Avg * 10) / 10;

// Tạo payload cho Template node (chỉ 1 output)
msg.payload = {
    pm25_avg: pm25Avg,
    pm10_avg: pm10Avg,
    pm25_count: pm25Values.length,
    pm10_count: pm10Values.length
};

return msg;

var username = msg.payload.username;
msg.original_password = msg.payload.password; // Lưu pass gốc để so sánh
msg.method = "GET";
msg.url = "https://iot-user-4e30c-default-rtdb.asia-southeast1.firebasedatabase.app/users/" + username + ".json";
return msg;

if (!msg.payload) {
    msg.payload = { status: "Log:   ❌ User not found", loggedIn: false };
    context.set("loggedIn", false);
    return [null, msg];
}

if (msg.payload.password === msg.original_password) {
    msg.payload = { status: "✅ Login successful", loggedIn: true };
    context.set("loggedIn", true);
    return [msg, null];
} else {
    msg.payload = { status: "Log:   ❌ Wrong password", loggedIn: false };
    context.set("loggedIn", false);
    return [null, msg];
}

if (!msg.payload || !msg.payload.feeds) {
    msg.payload = [];
    return msg;
}

const feeds = msg.payload.feeds;
let tableData = [];

feeds.forEach(feed => {
    const timestamp = new Date(feed.created_at);
    const timeString = timestamp.toLocaleString('vi-VN', {
        day: '2-digit',
        month: '2-digit', 
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        timeZone: 'Asia/Ho_Chi_Minh'
    });

    tableData.push({
        "Time": timeString,
        "PM25": feed.field1,  // Thay đổi key thành "PM25"
        "PM10": feed.field2,
        "Temperature": feed.field3,
        "Humidity": feed.field4
    });
});

tableData.reverse();
msg.payload = tableData;
return msg;

// CÁCH MỚI - Không dùng msg.url nữa
// Chỉ chuẩn bị dữ liệu để xử lý

msg.payload = {
    action: "getData",
    timestamp: new Date().toISOString()
};

// Log để debug
node.log("Preparing to fetch ThingSpeak data");

return msg;

// Lấy dữ liệu từ ThingSpeak response
var data = msg.payload;

if (data && data.feeds && data.feeds.length > 0) {
    // Lấy dữ liệu mới nhất
    var latestData = data.feeds[data.feeds.length - 1];

    // Tạo object dữ liệu
    var sensorData = {
        pm25: parseFloat(latestData.field1) || 0,
        pm10: parseFloat(latestData.field2) || 0, 
        temp: parseFloat(latestData.field3) || 0,
        hum: parseFloat(latestData.field4) || 0,
        timestamp: latestData.created_at,
        allFeeds: data.feeds,
        lastUpdate: new Date().toISOString()
    };

    // ✅ SỬA: Dùng GLOBAL context thay vì flow context
    global.set("latestSensorData", sensorData);

    // Log thông tin
    node.log("✅ Updated GLOBAL sensor data: PM2.5=" + sensorData.pm25 + 
             ", PM10=" + sensorData.pm10 + 
             ", Temp=" + sensorData.temp + 
             ", Hum=" + sensorData.hum);

    msg.payload = sensorData;
} else {
    msg.payload = {error: "No data received from ThingSpeak"};
    node.log("❌ No data from ThingSpeak API");
}

return msg;

//context.set("currentQuestion", msg.payload);
// Lấy câu hỏi từ user
var userQuestion = msg.payload;
msg.original_question = userQuestion;

// Đọc từ GLOBAL context
var sensorData = global.get("latestSensorData");

// Kiểm tra dữ liệu
if (!sensorData) {
    msg.payload = "Chưa có dữ liệu cảm biến. Hệ thống đang cập nhật dữ liệu mỗi 5 phút. Vui lòng chờ ít phút và thử lại.";
    return msg;
}

// Kiểm tra độ fresh của data - SỬA CÁCH TÍNH
var currentTime = new Date();
var lastUpdateTime = new Date(sensorData.lastUpdate);
var dataAge = currentTime.getTime() - lastUpdateTime.getTime();
var ageMinutes = Math.floor(dataAge / (1000 * 60));

// Kiểm tra data không quá cũ
if (ageMinutes > 15) {
    msg.payload = "Dữ liệu đã cũ " + ageMinutes + " phút. Đang chờ cập nhật mới...";
    return msg;
}

// Tạo prompt cho AI
var prompt = "Bạn là chuyên gia phân tích chất lượng không khí IoT.\n\n";
prompt += "DỮLIỆU HIỆN TẠI (cập nhật " + ageMinutes + " phút trước):\n";
prompt += "• PM2.5: " + sensorData.pm25 + " µg/m³\n";
prompt += "• PM10: " + sensorData.pm10 + " µg/m³\n";
prompt += "• Nhiệt độ: " + sensorData.temp + "°C\n";
prompt += "• Độ ẩm: " + sensorData.hum + "%\n";
prompt += "• Thời gian: " + sensorData.timestamp + "\n\n";

prompt += "TIÊU CHUẨN WHO:\n";
prompt += "PM2.5: <15 (tốt) | 15-25 (trung bình) | 25-37 (kém) | >37 (nguy hiểm)\n";
prompt += "PM10: <45 (tốt) | 45-75 (trung bình) | 75-150 (kém) | >150 (nguy hiểm)\n\n";

prompt += "CÂU HỎI: " + userQuestion + "\n\n";
prompt += "YÊU CÀU: Phân tích dữ liệu, đánh giá mức độ và đưa khuyến nghị cụ thể. Trả lời ngắn gọn bằng tiếng Việt.";

// Chuẩn bị cho Gemini
msg.payload = {
    contents: [{
        parts: [{
            text: prompt
        }]
    }],
    generationConfig: {
        temperature: 0.2,
        maxOutputTokens: 400
    }
};

return msg;

try {
    var response = msg.payload;

    if (response && response.candidates && response.candidates[0]) {
        var aiResponse = response.candidates[0].content.parts[0].text;

        // Format response với timestamp
        var formattedResponse = `🤖 **Phân tích chất lượng không khí:**\n\n${aiResponse}\n\n⏰ *Cập nhật: ${new Date().toLocaleString('vi-VN')}*`;

        msg.payload = formattedResponse;
    } else {
        msg.payload = "❌ Xin lỗi không thể phân tích dữ liệu lúc này. Vui lòng thử lại sau.";
    }
} catch (error) {
    msg.payload = "❌ Có lỗi xảy ra: " + error.message;
    node.error("AI Response Error: " + error.message);
}

return msg;

// ==== LẤY & KIỂM TRA DỮ LIỆU ==== //
const toNum = v => (v === undefined ? undefined : Number(v));

let humidity = toNum(flow.get("Humidity"));
let temp = toNum(flow.get("Temperature"));
let pm25 = toNum(flow.get("PM2_5"));
let pm10 = toNum(flow.get("PM10"));

if ([humidity, temp, pm25, pm10].some(v => v === undefined || Number.isNaN(v))) {
    node.status({ fill: "yellow", shape: "ring", text: "Chưa đủ/không hợp lệ dữ liệu" });
    return null;
}

// ==== NGƯỠNG CẢNH BÁO ==== //
const THRESHOLDS = {
    PM2_5: 35,       // μg/m³
    PM10: 50,        // μg/m³
    Temperature: 35, // °C
    Humidity: 80     // %
};

// ==== KIỂM TRA VƯỢT NGƯỠNG (KHÔNG CÓ COOLDOWN) ==== //
let alerts = [];

// PM2.5
if (pm25 > THRESHOLDS.PM2_5) {
    alerts.push({
        type: "PM2.5",
        value: pm25,
        threshold: THRESHOLDS.PM2_5,
        severity: pm25 > 75 ? "NGHIÊM TRỌNG" : "CẢNH BÁO",
        icon: "🔴",
        message: `PM2.5 vượt ngưỡng an toàn: ${pm25} μg/m³ (An toàn: < ${THRESHOLDS.PM2_5} μg/m³)`
    });
}

// PM10
if (pm10 > THRESHOLDS.PM10) {
    alerts.push({
        type: "PM10",
        value: pm10,
        threshold: THRESHOLDS.PM10,
        severity: pm10 > 100 ? "NGHIÊM TRỌNG" : "CẢNH BÁO",
        icon: "🔴",
        message: `PM10 vượt ngưỡng an toàn: ${pm10} μg/m³ (An toàn: < ${THRESHOLDS.PM10} μg/m³)`
    });
}

// Nhiệt độ
if (temp > THRESHOLDS.Temperature) {
    alerts.push({
        type: "Nhiệt độ",
        value: temp,
        threshold: THRESHOLDS.Temperature,
        severity: temp > 40 ? "NGHIÊM TRỌNG" : "CẢNH BÁO",
        icon: "🌡️",
        message: `Nhiệt độ quá cao: ${temp}°C (An toàn: < ${THRESHOLDS.Temperature}°C)`
    });
}

// Độ ẩm
if (humidity > THRESHOLDS.Humidity) {
    alerts.push({
        type: "Độ ẩm",
        value: humidity,
        threshold: THRESHOLDS.Humidity,
        severity: humidity > 90 ? "NGHIÊM TRỌNG" : "CẢNH BÁO",
        icon: "💧",
        message: `Độ ẩm quá cao: ${humidity}% (An toàn: < ${THRESHOLDS.Humidity}%)`
    });
}

// ==== KHÔNG VƯỢT NGƯỠNG -> KHÔNG GỬI ==== //
if (alerts.length === 0) {
    node.status({ fill: "green", shape: "dot", text: "Tất cả thông số bình thường" });
    return null;
}

// ==== BUILD TEXT/EMAIL ==== //
const textLines = alerts.map(a => `${a.icon} [${a.severity}] ${a.type}: ${a.message}`);
const text = [
    "CẢNH BÁO MÔI TRƯỜNG",
    `Thời điểm: ${new Date().toLocaleString()}`,
    `Vị trí: Phòng giám sát IoT`,
    "",
    ...textLines,
    "",
    "Giá trị hiện tại:",
    `• PM2.5: ${pm25} μg/m³`,
    `• PM10 : ${pm10} μg/m³`,
    `• Nhiệt độ: ${temp} °C`,
    `• Độ ẩm : ${humidity} %`
].join("\n");

node.status({ fill: "red", shape: "dot", text: `${alerts.length} cảnh báo` });

// Nếu email node của bạn dùng msg.payload là text:
msg.topic = "[IoT] Cảnh báo môi trường";
msg.payload = text;

// Nếu phía sau bạn cần thêm JSON cho node khác:
msg.details = {
    alerts,
    timestamp: new Date().toISOString(),
    location: "Phòng giám sát IoT",
    allValues: { pm25, pm10, temperature: temp, humidity }
};

return msg;

let alertData = msg.payload;

// Firebase Realtime Database URL
let firebaseUrl = "https://iot-user-4e30c-default-rtdb.asia-southeast1.firebasedatabase.app/emails.json";

// Chuẩn bị HTTP request
msg.url = firebaseUrl;
msg.method = "GET";
msg.headers = {
    "Content-Type": "application/json"
};

// Lưu alert data để sử dụng ở node tiếp theo
msg.alertData = alertData;

return msg;

// ===== 1) Gom email như bạn đang làm =====
const data = msg.payload;
const reEmail = /^[^\s@]+@[^\s@]+\.[^\s@]+$/i;

const found = new Set();
(function collect(v){
  if (!v) return;
  if (typeof v === "string") { const s=v.trim(); if (reEmail.test(s)) found.add(s); return; }
  if (Array.isArray(v)) { v.forEach(collect); return; }
  if (typeof v === "object") {
    if (typeof v.email === "string" && reEmail.test(v.email.trim())) found.add(v.email.trim());
    Object.values(v).forEach(collect);
  }
})(data);

const emails = [...found];
if (!emails.length) { node.status({fill:"yellow",shape:"ring",text:"Không tìm thấy email"}); return null; }

// ===== 2) Build nội dung từ msg.alertData (string hoặc object) =====
const ad = msg.alertData || {};
let textContent = "";
let htmlContent = undefined;

if (typeof ad === "string") {
  textContent = ad; // đã là text sẵn
} else {
  const alerts = Array.isArray(ad.alerts) ? ad.alerts : [];
  const ts  = new Date(ad.timestamp || Date.now());
  const loc = ad.location || "N/A";
  const vals = ad.allValues || {};
  const pm25 = vals.pm25 ?? "—";
  const pm10 = vals.pm10 ?? "—";
  const temp = vals.temperature ?? "—";
  const hum  = vals.humidity ?? "—";

  const lines = alerts.length
    ? alerts.map(a => `${a.icon || ""} [${a.severity || ""}] ${a.message || ""}`).join("\n")
    : "(không có chi tiết)";

  textContent =
`🚨 CẢNH BÁO CHẤT LƯỢNG KHÔNG KHÍ
📍 Vị trí: ${loc}
🕐 Thời gian: ${ts.toLocaleString('vi-VN')}

${lines}

📊 TẤT CẢ THÔNG SỐ HIỆN TẠI:
• PM2.5: ${pm25} μg/m³
• PM10 : ${pm10} μg/m³
• Nhiệt độ: ${temp} °C
• Độ ẩm : ${hum} %`;

  htmlContent = `<!DOCTYPE html><html lang="vi"><head>
<meta charset="UTF-8"><meta name="viewport" content="width=device-width,initial-scale=1">
<title>Cảnh báo môi trường</title>
<style>
 body{font-family:Arial,Helvetica,sans-serif;background:#f5f7fb;margin:0;padding:20px;color:#222}
 .card{max-width:680px;margin:0 auto;background:#fff;border-radius:12px;overflow:hidden;box-shadow:0 8px 24px rgba(0,0,0,.08)}
 .hdr{background:linear-gradient(135deg,#ff6b6b,#ff5252);color:#fff;padding:20px}
 .hdr h1{margin:0 0 4px 0;font-size:22px}
 .meta{opacity:.95;font-size:14px}
 .sec{padding:20px}
 .sec h2{margin:0 0 10px 0;font-size:18px}
 .alert{border-left:4px solid #ffc107;background:#fff3cd;padding:12px 14px;border-radius:8px;margin:10px 0}
 .alert.crit{border-left-color:#dc3545;background:#f8d7da}
 table{width:100%;border-collapse:collapse;margin-top:14px}
 th,td{border:1px solid #e9ecef;padding:10px;text-align:left;font-size:14px}
 th{background:#f8f9fa}
 .ok{color:#28a745;font-weight:600}.warn{color:#ffc107;font-weight:600}.crit{color:#dc3545;font-weight:600}
 .footer{background:#f8f9fa;color:#666;font-size:12px;padding:12px 16px;text-align:center}
</style></head><body>
<div class="card">
  <div class="hdr">
    <h1>🚨 CẢNH BÁO CHẤT LƯỢNG KHÔNG KHÍ</h1>
    <div class="meta">📍 ${loc} • 🕐 ${ts.toLocaleString('vi-VN')}</div>
  </div>
  <div class="sec">
    <h2>⚠️ Chi tiết cảnh báo</h2>
    ${alerts.map(a => `
      <div class="alert ${a.severity === 'NGHIÊM TRỌNG' ? 'crit' : ''}">
        <strong>${a.icon || ""} ${a.type || ""} — ${a.severity || ""}</strong><br/>
        ${a.message || ""}
      </div>`).join('') || '<div class="alert">Không có chi tiết</div>'}
  </div>
  <div class="sec">
    <h2>📊 Thông số hiện tại</h2>
    <table role="presentation">
      <tr><th>Thông số</th><th>Giá trị</th><th>Ngưỡng</th><th>Trạng thái</th></tr>
      <tr><td>PM2.5</td><td>${pm25} μg/m³</td><td>&lt; 35</td><td class="${Number(pm25)>35?'crit':'ok'}">${Number(pm25)>35?'❌ Vượt ngưỡng':'✅ Bình thường'}</td></tr>
      <tr><td>PM10</td><td>${pm10} μg/m³</td><td>&lt; 50</td><td class="${Number(pm10)>50?'crit':'ok'}">${Number(pm10)>50?'❌ Vượt ngưỡng':'✅ Bình thường'}</td></tr>
      <tr><td>Nhiệt độ</td><td>${temp} °C</td><td>&lt; 35</td><td class="${Number(temp)>35?'crit':'ok'}">${Number(temp)>35?'❌ Quá nóng':'✅ Bình thường'}</td></tr>
      <tr><td>Độ ẩm</td><td>${hum} %</td><td>&lt; 80%</td><td class="${Number(hum)>80?'warn':'ok'}">${Number(hum)>80?'⚠️ Quá ẩm':'✅ Bình thường'}</td></tr>
    </table>
  </div>
  <div class="footer">Hệ thống giám sát IoT • Email tự động • Gửi lúc ${new Date().toLocaleString('vi-VN')}</div>
</div>
</body></html>`;
}

// ===== 3) DỌN RÁC TRƯỚC KHI GỬI QUA EMAIL NODE =====
// Xoá các header/cờ có thể khiến nodemailer set sai Content-Type
delete msg.headers;        // <- QUAN TRỌNG: bạn đã set ở bước HTTP là application/json
delete msg.attachments;
delete msg.filename;
delete msg.contentType;
delete msg.encoding;

// ===== 4) Gán trường cho Email node =====
msg.to     = emails.join(", ");                 // để trống ô "To" trong Email node
msg.topic  = "[IoT] Cảnh báo môi trường";      // subject
msg.payload = String(textContent || "");        // PLAIN TEXT (bắt buộc là chuỗi)
if (htmlContent) msg.html = htmlContent;       // HTML body (tuỳ chọn)

node.status({fill:"green", shape:"dot", text:`Sẽ gửi tới ${emails.length} email`});
return msg;
